import styled from "styled-components"
import {
  Colors
} from "constants"
import {
  ListGroup,
  Image,
  Container
} from "react-bootstrap"
import {
  faListUl,
  faStar,
  faHeadset,
  faShoppingBasket,
  faMoneyBillWave,
  faCog,
  faIdCard,
  faQuestion
} from "@fortawesome/free-solid-svg-icons";
import ListGroupItem from "./ListGroupItem"

const Sidebar = () => {
  return (
    <Wrapper>
      <PerfilContainer>
        <PefilImage src="data:image/jpeg;base64," roundedCircle />
        <PerfilInfoContainer>
          <h6>Lavanderia do zé</h6>
        </PerfilInfoContainer>
      </PerfilContainer>
      <ListGroup>
        {
          menus.map((menu) => {
            return (
              <ListGroupItem
                label={menu.label}
                icon={menu.icon}
                active={menu.active}
                eventKey={menu.eventKey}
              />
            )
          })
        }
      </ListGroup>
    </Wrapper>
  )
}

const menus = [
  {
    label: "Início",
    icon: null,
    active: false,
    eventKey: "",
  },
  {
    label: "Serviços",
    icon: faListUl,
    active: false,
    eventKey: "services",
  },
  {
    label: "Pedidos",
    icon: faShoppingBasket,
    active: false,
    eventKey: "",
  },
  {
    label: "Avaliações",
    icon: faStar,
    active: false,
    eventKey: "",
  },
  {
    label: "Pagamento",
    icon: faMoneyBillWave,
    active: false,
    eventKey: "",
  },
  {
    label: "Perfil",
    icon: faIdCard,
    active: false,
    eventKey: "",
  },
  {
    label: "Configurações",
    icon: faCog,
    active: false,
    eventKey: "",
  },
  {
    label: "Suporte",
    icon: faHeadset,
    active: false,
    eventKey: "",
  },
  {
    label: "Ajuda",
    icon: faQuestion,
    active: false,
    eventKey: "",
  },
]

const PerfilContainer = styled(Container)`
  display: flex;
  padding: 10px;
`

const PefilImage = styled(Image)`
  height: 60px;
  width: 60px;
  margin: 10px;
`

const PerfilInfoContainer = styled(Container)`
  display: flex;
  justify-content: center;
  align-items: center;
`

const Wrapper = styled.div`
  height: 94vh;
  background-color: ${Colors.light};
  position: sticky;
  position: -webkit-sticky; /* Safari */
  top: 6vh;
`

export default Sidebar
